import React from 'react';
import { ExternalLink, DollarSign, Tag } from 'lucide-react';
import './Results.css';

const Results = ({ results, loading, uploadedImage }) => {
  if (loading) {
    return (
      <div className="results-section card">
        <div className="loading-container">
          <div className="loading-spinner large"></div>
          <h3>Analyzing your image...</h3>
          <p>Our AI is finding similar furniture items for you</p>
        </div>
      </div>
    );
  }

  if (!results) return null;

  if (results.status !== 'success') {
    return (
      <div className="results-section card">
        <div className="no-results">
          <h3>No furniture detected</h3>
          <p>{results.status}</p>
          <p>Try uploading an image with clear furniture items like chairs, tables, or sofas.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="results-section">
      <div className="results-header card">
        <h2>Search Results</h2>
        {uploadedImage && (
          <div className="uploaded-preview">
            <img src={uploadedImage} alt="Your upload" />
            <span>Your uploaded image</span>
          </div>
        )}
      </div>

      {results.results.map((category, categoryIndex) => (
        <div key={categoryIndex} className="category-section card fade-in">
          <div className="category-header">
            <Tag className="category-icon" />
            <h3>Similar {category.label}s</h3>
            <span className="match-count">{category.matches.length} matches</span>
          </div>

          <div className="matches-grid grid grid-3">
            {category.matches.map((match, matchIndex) => (
              <div key={matchIndex} className="match-card">
                <div className="match-image-container">
                  <img
                    src={`file://${match.image_path}`}
                    alt={`Similar ${category.label}`}
                    className="match-image"
                    onError={(e) => {
                      e.target.style.display = 'none';
                      e.target.nextSibling.style.display = 'flex';
                    }}
                  />
                  <div className="image-error" style={{ display: 'none' }}>
                    <p>Image not available</p>
                  </div>
                </div>

                <div className="match-info">
                  <div className="similarity-score">
                    <span className="score-label">Similarity:</span>
                    <span className="score-value">
                      {((1 - match.distance) * 100).toFixed(1)}%
                    </span>
                  </div>

                  {match.price && (
                    <div className="price-info">
                      <DollarSign size={16} />
                      <span>{match.price}</span>
                    </div>
                  )}

                  {match.external_url && (
                    <a
                      href={match.external_url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="external-link"
                    >
                      <ExternalLink size={16} />
                      View Product
                    </a>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Results;
